import axios from "axios";
import React, { useState } from "react";
import { Form, FormGroup } from "react-bootstrap";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const AppointmentReservation = () => {
  const [doctorName, setDoctorName] = useState(" ");
  const [dateLower, setDateLower] = useState(new Date());
  const [dateUpper, setDateUpper] = useState(new Date());
  const [prioritiseDoctor, setPrioritiseDoctor] = useState(false);
  const [prioritiseDate, setPrioritiseDate] = useState(false);
  let loadedAppointmentsList = [];

  let onSubmit = (event) => {
    event.preventDefault();
    let priority = "";
    if (prioritiseDoctor == true) {
      priority = "doctor";
    } else if (prioritiseDate == true) {
      priority = "time";
    }
    const data = {
      DoctorName: doctorName,
      DateLower: dateLower,
      DateUpper: dateUpper,
      Priority: priority,
    };
    const url = "http://localhost:56210/appointment/findByPriority";
    axios
      .post(url, data, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
      .then((response) => {
        for (let item of response.data.list) {
          loadedAppointmentsList.push(item);
        }
        console.log(loadedAppointmentsList);
      });
  };

  useEffect(() => {
    <ul>
      {loadedAppointmentsList.map((item) => (
        <li key={item.Id}>{item.Date}</li>
      ))}
    </ul>;
  });

  return (
    <div>
      <Form style={{ width: "350px" }} onSubmit={onSubmit}>
        <FormGroup>
          <input
            type="text"
            onChange={(event) => setDoctorName(event.target.value)}
            placeholder="Doctor name"
            name="doctorName"
          ></input>
        </FormGroup>
        <FormGroup>
          <DatePicker
            selected={dateLower}
            onChange={(date) => setDateLower(date)}
            dateFormat="yyyy/MM/dd"
          />
        </FormGroup>
        <FormGroup>
          <DatePicker
            selected={dateUpper}
            onChange={(date) => setDateUpper(date)}
            dateFormat="yyyy/MM/dd"
          />
        </FormGroup>

        <FormGroup>
          <div class="form-check">
            <input
              class="form-check-input"
              type="checkbox"
              value=""
              id="doctor"
              onChange={(event) => setPrioritiseDoctor(event.target.checked)}
            />
            <label class="form-check-label" for="doctor">
              Prioritise doctor
            </label>
          </div>
        </FormGroup>

        <FormGroup>
          <div class="form-check">
            <input
              class="form-check-input"
              type="checkbox"
              value=""
              id="date"
              onChange={(event) => setPrioritiseDate(event.target.checked)}
            />
            <label class="form-check-label" for="date">
              Prioritise date
            </label>
          </div>
        </FormGroup>

        <FormGroup>
          <button type="submit">Filter</button>
        </FormGroup>
      </Form>
    </div>
  );
};

export default AppointmentReservation;
